db:select.db:sql:
SELECT room_id
FROM Room
WHERE room_size = :room_size;

ee:message.ee:set-payload:
output application/json

var total_nights = daysBetween(payload.reserved_date_start as Date, payload.reserved_date_end as Date)
var total_price = (total_nights * payload.reserved_room.price as Number) as String {format: "0.00"}
var reservation_id = 
    payload.guest_name as String replace /[^a-zA-Z]/ with "_"
    ++ "_" ++ payload.reserved_room.room_size as String
    ++ "_" ++ total_nights as String
    ++ "_" ++ vars.currentID as String
---
payload
++ {"reservation_id": reservation_id as String}
++ {"total_nights": total_nights as Number}
++ {"total_price": total_price as Number}

set-payload.value:
#[output application/json
---
{
	"message": "Reservation created successfully! Your ID is: " ++ vars.reservationID as String
}]

ee:message.ee:set-payload:
%dw 2.0
output application/json
---
{
	message: "Reservation with ID: " ++ attributes.uriParams.reservation_id as String ++ " deleted!"
}

set-payload.value:
#[output application/json
---
{
	"message": "ALL RESERVATIONS AND CONFIG VALUES WERE DELETED!"
}]

#[output application/json
---
{
	"message": "Config Values Object Store initialized with a starting ID of 1 available at: 'currentID' key"
}]
